        </Form.Row>

         

          <Form.Row>

          <Form.Group as={Col} >
                    <Form.Label>Username</Form.Label>
                    <Form.Control type="username" 
                     placeholder="Enter your username" 
                     id = "username"
                     className = "form-control"
                     onChange = {this.handleNameChange}
                     
                     />
                 </Form.Group>

                 <Form.Group as={Col} >
                    <Form.Label>Phone number</Form.Label>
                    <Form.Control type="number" 
                     placeholder="Enter your phone number" 
                     id = "phonenumber"
                     className = "form-control"
                     onChange = {this.handlePhoneNumberChange}
                     
                     />
                 </Form.Group>

                 </Form.Row>
                 <Form.Row>
                 <ProgressButton 
                
        onClick={this.handlelocationLostChange.bind(this)}>Share location where pet is lost</ProgressButton>
                


                  
          <Form.Group as={Col} >
                    <Form.Label>Email</Form.Label>
                    <Form.Control type="email" 
                     placeholder="Enter email" 
                     id = "email"
                     className = "form-control"
                     onChange = {this.handleEmailChange}
                     
                     
                     />
                 </Form.Group>
                 </Form.Row>
        
                  

                  
                  <Form.Row>
                  
                  <Form.Group as={Col}>
                    <Form.Label>Any other information you would like to share ?</Form.Label>
                    <textarea type="otherDescription" 
                     placeholder="Tell us more..." 
                     id = "otherDescription"
                     className = "form-control"
                     onChange = {this.handleotherDescriptionChange}
                    rows = {3}
                     
                     />
                 </Form.Group>
        
                  </Form.Row>
                  <button
                            id="1"
                            name="btn"
                            type="submit"
                            onClick= {this.RegisterClickHandler}
                            className = "btn btn-success btn-block"
                            
                        >
                            Submit
                            
                        </button>

         
          </Form>
          </div>
            
                 
                 </div > 
                 
                 
    );
    }

}
