import React, { Component } from 'react';
import { getUsers, logIn } from "../actions/actions";
import '../css/Table.css';
import '../App.css';

class Loginpage extends Component {
    constructor() {
        super();
        //this.authenticatorr = this.authenticatorr.bind(this);
        this.state = {
            user_id: "",
            username: "",
            password: "",
            data: []
        };
    }

    componentDidMount() {
    this.retrieveUsers();
    }

    retrieveUsers = () => {
        getUsers().then((res) =>{
        this.setState({data: res});
        console.log(res);
    });
}

    
    handleNameChange = (event) => {
        event.preventDefault();
        this.setState({ username: event.target.value });
    };

    handlePasswordChange = (event) => {
        event.preventDefault();
        this.setState({ password: event.target.value });
    };

    // authenticator = (event) => {
    //     const {username} = this.state;
    //     const{password} = this.state;
      
    //     const contents = this.state.data.forEach((item,key)=> {
            
    //         if(item.username === username && item.password === password ){
    //             alert("Welcome!")    
    //             this.props.history.push("/"); 
    //             return;
    //         } 
    //         else if (item.username !== username && item.password !== password) {

    //             alert("wrong username or password");
    //             this.props.history.push("/login"); 
               
    //         }
            
            
    //     });
        
        
        
    // };


    authenticatorr = (event) => {
        event.preventDefault();
        const {username} = this.state;
        const{password} = this.state;
        let data = {
            username: username,
            password: password,
        }
        logIn(data).then((res) =>{
             try{
                
            
                 alert("You have successfully logged in");
                 this.props.history.push("/register");
             
             
             }catch(error){
                 console.log("Incorret username or passowrd" + error);
                
             }
             
        })
        
        
    };



    render() {
        return (

            
            <div className="row mt-3">
                
                <div className="col-md-6-m-auto">
               
                    <div className="card card-body">
                    <li className="navbar-brand">
                     <a className="navbar-brand" href="/Home">
                      <img src="pharmacylogo.png" width="100%" height="100%" className="floatright" alt=""></img>
                    </a>
                     </li>
                        <h1 className="text-center mb-3">
                            <i className="fas fa-sign-in-alt">

                           
                            </i>
                           
                Login
                </h1>

                
                
                        <div className="form-group">
                            <label>username</label>
                            <input
                                type="username"
                                id = "username"
                                className="form-control"
                                onChange = {this.handleNameChange}
                           />
                        </div>
                        <div className="form-group">
                            <label>password</label>
                            <input
                                type="password"
                                id="password"
                                className="password"
                                className="form-control"
                                onChange= {this.handlePasswordChange}
                         />
                        </div>

                        <div>
                         <img src="PharmacyDesign.jpg" width = "100% height = 100%"></img>
                          </div>

                        <button
                            type="submit"
                            onClick= {this.authenticatorr}
                            className = "btn btn-success"
                        >
                            Login
                        
                        </button>



                        <p className = "lead mt-4">
                            Create Account <a href = "/register">Register</a>
                        </p>
                        

                 </div>
                 </div >
                 </div > 
                 
    );
    }

}

export default Loginpage;
