import React, { Component } from 'react';
import { getBmi } from "../actions/actions";
import {Link} from 'react-router-dom'
import '../App.css';
import { deleteBmi } from "../actions/actions";

class BmiDetail extends Component {
    constructor(){
        super();
        this.deleteRecord = this.deleteRecord.bind(this);
        this.state = {
          data: []
        };
    }
   
    componentDidMount() {
        this.retrieveBmi();
    }

    retrieveBmi = () => {
        getBmi().then((res) =>{
        this.setState({data: res});
        console.log(res);
    });
}



deleteRecord = (event) => {
    event.preventDefault();
    let id = event.target.id;
    console.log(id);
    deleteBmi(id).then(res=>{
        alert("Bmi deleted");
    }).catch(err=>{
        alert("error deleting");
    });
};


    render() {
        let rows = [];

        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(
              <thead>
            <tr className='bg-secondary text-white'>
            <td>{item.id}</td>
            <td>{item.height}</td>
            <td>{item.weight}</td>
            <td>{item.bmiResult}</td>
            <td>{item.bmiClass}</td>
            {/* <td>{item.user_id}</td> */}
            <td><button id={item.id} onClick={this.deleteRecord}>Delete</button></td>
            </tr>
            </thead>
            );
          }else{
            rows.push(
            <thead>
            <tr className='bg-dark text-light'>
            <td>{item.id}</td>
            <td>{item.height}</td>
            <td>{item.weight}</td>
            <td>{item.bmiResult}</td>
            <td>{item.bmiClass}</td>
            {/* <td>{item.user_id}</td> */}
            <td><button id={item.id} onClick={this.deleteRecord}>Delete</button></td>
            </tr>
            </thead>
            
            );
            
          }
     })
        return (
          
          <div className="container">
            
        <img src="bmiIcon" className="center" alt=""></img>
    
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                <th>id</th>
                <th>height</th>
                <th>weight</th>
                <th>bmiResult</th>
                <th>bmiClass</th>
                <th>Delete</th>
              </tr>
              </thead>
              {rows}
            </table>
                       <Link to='/BmiPage'>
                         <button type="button" className="btn btn-dark btn-lg btn-block">Add New Bmi record</button>
                        
                       </Link>
     
            <div className="row">
            </div>
          </div>
        );
      }
}


export default BmiDetail;
