          <Button
            className="btn-danger btn-sm"
            onClick={() => this.deleteRecord(cell)}
          >
            Delete
          </Button>
        </div>
       )
    }

    retrieveUsers = () => {
        getUsers().then((res) =>{
        this.setState({data: res});
        console.log(res);
    });
}

addRecord = () => event => {
    event.preventDefault();
