        imageid: imageid,
        image: image, 
       }
       this.props.dispatch(actions.addImage(data));
       if(this.props.createMessage){
        alert("Healthcare product Added");
        this.redirectToHome()
    }
    else {
        alert("Couldnt Healthcare product")
    }
    };
    redirectToHome = () => {
        const { history } = this.props;
        if(history) history.push('/Home');
      }

