import React, { Component,useState,Switch,Text,AsyncStorage} from 'react';
import { getUsers, logIn } from "../actions/actions";
import '../css/Table.css';
import '../App.css';
import { GoogleLogin,responseGoogle } from 'react-google-login';
import { useGoogleLogin,onRequest,jsSrc,responseType,accessType,scope,uxMode,onFailure,redirectUri,discoveryDocs,isSignedIn,autoLoad,hostedDomain,loginHint,fetchBasicProfile,clientId,cookiePolicy,onAutoLoadFinished,onSuccess,loaded,signIn } from 'react-google-login'

class Loginpage extends Component {
    
    constructor() {
        
        super();
        
        this.state = {
            username: "",
            password: "",
            rememberMe: false,
            data: [],
        };
        
    }

    // componentDidMount() {
        
    
    
    // }
    async componentDidMount() {
        this.retrieveUsers();
        const username = await this.getRememberedUser();
        this.setState({ 
           username: username || "", 
           rememberMe: username ? true : false });
        }

    retrieveUsers = () => {
        getUsers().then((res) =>{
        this.setState({data: res});
        console.log(res);
    });
}
toggleRememberMe = value => {
    this.setState({ rememberMe: value })
      if (value === true) {
    //user wants to be remembered.
      this.rememberUser();
    } else {
      this.forgetUser();
    }
  }
  rememberUser = async () => {
    try {
      await AsyncStorage.setItem('key', this.state.username);
    } catch (error) {
      // Error saving data
    }
    };
    getRememberedUser = async () => {
    try {
      const username = await AsyncStorage.getItem('key');
      if (username !== null) {
        // We have username!!
        return username;
      }
    } catch (error) {
      // Error retrieving data
    }
    };
    forgetUser = async () => {
      try {
        await AsyncStorage.removeItem('Longtail-User');
      } catch (error) {
       // Error removing
      }
    };
    handleNameChange = (event) => {
        event.preventDefault();
        this.setState({ username: event.target.value });
    };

    handlePasswordChange = (event) => {
        event.preventDefault();
        this.setState({ password: event.target.value });
    };

    // authenticator = (event) => {
    //     const {username} = this.state;
    //     const{password} = this.state;
      
    //     const contents = this.state.data.forEach((item,key)=> {
            
    //         if(item.username === username && item.password === password ){
    //             alert("Welcome!")    
    //             this.props.history.push("/"); 
    //             return;
    //         } 
    //         else if (item.username !== username && item.password !== password) {

    //             alert("wrong username or password");
    //             this.props.history.push("/login"); 
               
    //         }
            
            
    //     });
        
        
        
    // };


    authenticatorr = (event) => {
        event.preventDefault();
        const {username} = this.state;
        const{password} = this.state;
       
        let data = {
            username: username,
            password: password,
        }
        logIn(data).then((res) =>{
            console.log(res)
            
             if(res === true){
                localStorage.setItem('username', username)
                console.log(username)
                 alert("You have successfully logged in");
                 this.props.history.push("/Home");
             }
             else { 
                 alert("wrong username or password");
             }
             
        })
        
        
    };


    
    render() {
        return (

            
            <div className="row mt-3">
                
                <div className="col-md-6-m-auto">
               
                    <div className="card card-body">
                    <li className="navbar-brand">
                     <a className="navbar-brand" href="/Home">
                      <img src="pharmacylogo.png" width="100%" height="100%" className="floatright" alt=""></img>
                    </a>
                     </li>
                        <h1 className="text-center mb-3">
                            <i className="fas fa-sign-in-alt">

                           
                            </i>
                           
                Login
                </h1>

                
                
                        <div className="form-group">
                            <label>username</label>
                            <input
                                type="username"
                                id = "username"
                                className="form-control"
                                onChange = {this.handleNameChange}
                           />
                        </div>
                        <div className="form-group">
                            <label>password</label>
                            <input
                                type="password"
                                id="password"
                                className="password"
                                className="form-control"
                                onChange= {this.handlePasswordChange}
                         />
                        </div>

                        <div className="form-group">
                            <label>Remember me</label>
                            <input
                            value={this.state.rememberMe}
                                type="checkbox"
                                className="form-control"
                                onValueChange={(value) => this.toggleRememberMe(value)}
                         />
                        </div>
                        <script src="https://apis.google.com/js/platform.js" async defer></script>
                        <meta name="google-signin-client_id" content="539942293395-hf1k02msh1ud8jib0q2jnndc9nb0h28a.apps.googleusercontent.com.apps.googleusercontent.com"></meta>
                        {/* <GoogleLogin
  clientId="539942293395-hf1k02msh1ud8jib0q2jnndc9nb0h28a.apps.googleusercontent.com"
  onSuccess={responseGoogle}
  isSignedIn={true}
/> */}
<GoogleLogin

             clientId="539942293395-hf1k02msh1ud8jib0q2jnndc9nb0h28a.apps.googleusercontent.com"

                buttonText="Login with Google"

                onSuccess={responseGoogle}

                onFailure={responseGoogle} ></GoogleLogin>


                        <div>
                         <img src="PharmacyDesign.jpg" width = "100% height = 100%"></img>
                          </div>
                         
          
                        <button
                            id="1"
                            name="btn"
                            type="submit"
                            onClick= {this.authenticatorr}
                            className = "btn btn-success"
                        >
                            Login
                        
                        </button>

          
 

                        <p className = "lead mt-4">
                            Create Account <a href = "/registration">Register</a>
                        </p>
                        

                 </div>
                 </div >
                 </div > 
                 
    );
    }

}

export default Loginpage;
