import React, { Component } from "react";
import "../App.css";
import { Link } from "react-router-dom";
import { updateUser, createUser } from "../actions/actions";

class UserForm extends Component {
  constructor(props) {
    super(props);
    // This binding is necessary to make `this` work in the callback
    this.UserRegistrationFunction = this.UserRegistrationFunction.bind(this);
    this.state = { Username: "", Age: "", Occupation: "", Hobby: "" };
  }

  componentDidMount() {}

  handleNameChange = (event) => {
    event.preventDefault();
    this.setState({ Username: event.target.value });
  };

  handleAgeChange = (event) => {
    event.preventDefault();
    this.setState({ Age: event.target.value });
  };

  handleOccupation = (event) => {
    event.preventDefault();
    this.setState({ Occupation: event.target.value });
  };

  handleHobby = (event) => {
    event.preventDefault();
    this.setState({ Hobby: event.target.value });
  };

  UserRegistrationFunction = (event) => {
    event.preventDefault();
    const { Username } = this.state;
    const { Age } = this.state;
    const { Occupation } = this.state;
    const { Hobby } = this.state;
    let userID = "";

    if (this.props.location.state === undefined) {
      //add User
      createUser({ Username, Age, Occupation, Hobby })
        .then((res) => {
          this.props.history.push("/");
        })
        .catch((err) => {
          alert(err);
        });
    } else {
      userID = this.props.location.state.user_id;
      //update User the ID is set
      updateUser({ userID, Username, Age, Occupation, Hobby })
        .then((res) => {
          this.props.history.push("/");
        })
        .catch((err) => {
          alert(err);
        });
    }
  };
  render() {
    return (
      <div className="App">
        <h1>User Form</h1>
        <Link to="/"> Go back to Table </Link>
        <form
          onSubmit={this.UserRegistrationFunction}
          method="POST"
          className=""
        >
          <div className="form-group row">
            <label className="form-label" for="name">
              Name:
            </label>
            <input
              className="form-control"
              type="text"
              onChange={this.handleNameChange}
              id="name"
              name="name"
              placeholder={
                this.props.location.state !== undefined
                  ? this.props.location.state.user_name
                  : "Name"
              }
              required
              size="10"
            />
            <br />
          </div>
          <div className="form-group row">
            <label className="form-label" for="age">
              Age:
            </label>
            <input
              className="form-control"
              type="text"
              id="age"
              name="age"
              onChange={this.handleAgeChange}
              placeholder={
                this.props.location.state !== undefined
                  ? this.props.location.state.user_age
                  : "Age"
              }
              required
              size="10"
            />
            <br />
          </div>
          <div className="form-group row">
            <label className="form-label" for="occupation">
              Occupation:
            </label>
            <input
              className="form-control"
              type="text"
              id="occupation"
              name="occupation"
              placeholder={
                this.props.location.state !== undefined
                  ? this.props.location.state.user_occupation
                  : "Occupation"
              }
              onChange={this.handleOccupation}
              required
              size="10"
            />
            <br />
          </div>
          <div className="form-group row">
            <label className="form-label" for="hobby">
              Hobby:
            </label>
            <input
              className="form-control"
              type="text"
              id="hobby"
              name="hobby"
              placeholder={
                this.props.location.state !== undefined
                  ? this.props.location.state.user_hobby
                  : "Hobby"
              }
              required
              size="10"
              onChange={this.handleHobby}
            />
            <br />
          </div>
          <input
            className="btn bg-primary text-light"
            type="submit"
            value="Save"
          />
        </form>
      </div>
    );
  }
}

export default UserForm;
