import React, { Component } from 'react';
import {Link} from "react-router-dom";
import { adoptDog, getDogs } from "../actions/actions";
import { connect } from 'react-redux';
import actions from '../redux/actions/dogs';
import PhoneInput from 'react-phone-number-input/input'
import { Form, Button, FormGroup, FormControl, ControlLabel, Col } from "react-bootstrap";
import '../App.css';
class AdoptDogs extends Component {
    constructor() {
        super();
        this.state = {
            adoptDogid: "",
            username: "",
            address: "",
            email: "",
            phonenumber: "",
            DOB: "",
            house: "",
            familyDescription: "",
            animalAccess: "",
            pastCare: "",
            currentPets: "",
            description: "",
            


        };
    }

    componentDidMount() {
         this.props.dispatch(actions.getDogs());
         
    }
    RegisterClickHandler = () =>
    {
       const {adoptDogid} = this.state;
       const {username} = this.state;
       const {address} = this.state;
       const {email} = this.state;
       const {phonenumber} = this.state;
       const {DOB} = this.state; 
       const {house} = this.state;
       const {familyDescription} = this.state;
       const {animalAccess} = this.state;
       const {pastCare} = this.state;
       const {currentPets} = this.state;
       const {description} = this.state;
       let data = {
           adoptDogid: adoptDogid,
           username: username,
           address: address,
           email: email,
           phonenumber: phonenumber,
           DOB: DOB,
           house: house,
           familyDescription: familyDescription,
           animalAccess: animalAccess,
           pastCare: pastCare,
           currentPets: currentPets,
           description: description,
           
           
       }
       this.props.dispatch(actions.adoptDog(data));
       if(this.props.createMessage){
           alert("dog adopted");
           this.redirectToHome()
       }
       else {
           alert("Couldnt adopt dog")
       }
    };

    redirectToHome = () => {
        const { history } = this.props;
        if(history) history.push('/Home');
      }


    handleuseridChange = (event) => {
        event.preventDefault();
        this.setState({ adoptDogid: event.target.value });
    };


    handleNameChange = (event) => {
        event.preventDefault();
        this.setState({ username: event.target.value });
    };

    handleAddressChange = (event) => {
        event.preventDefault();
        this.setState({ address: event.target.value });
    };

    handleEmailChange = (event) => {
        event.preventDefault();
        this.setState({ email: event.target.value });
    };

    handlePhoneNumberChange = (event) => {
        event.preventDefault();
        this.setState({ phonenumber: event.target.value });
    };

    handleDOBChange = (event) => {
        event.preventDefault();
        this.setState({ DOB: event.target.value });
    };

    handleHouseChange = (event) => {
        event.preventDefault();
        this.setState({ house: event.target.value });
    };

    handlefamilyDescriptionChange = (event) => {
        event.preventDefault();
        this.setState({ familyDescription: event.target.value });
    };

    handleanimalAccessChange = (event) => {
        event.preventDefault();
        this.setState({ animalAccess: event.target.value });
    };

    handlepastCareChange = (event) => {
        event.preventDefault();
        this.setState({ pastCare: event.target.value });
    };

    handlecurrentPetsChange = (event) => {
        event.preventDefault();
        this.setState({ currentPets: event.target.value });
    };

    handledescriptionChange = (event) => {
        event.preventDefault();
        this.setState({ description: event.target.value });
    };

    

    

    


    render() {
        return (
            <div
            class="bg_image"
            style={{
              backgroundImage: 'url("petshelterbg2.jpg")',
              backgroundSize: "cover",
              height: "170vh",
              
            }}
          > 
<div className="form">
          <Form>
              <Form.Row>
                  
              <Form.Group as={Col} controlId="username">
                <Form.Label>Username</Form.Label>
                <Form.Control type="username" 
                 placeholder="Enter username" 
                 id = "username"
                 className = "form-control"
                 onChange = {this.handleNameChange}
                 
                 
                 />
             </Form.Group>
             
            
             <Form.Group as={Col} controlId="address">
      <Form.Label>Address</Form.Label>
      <Form.Control type="address" 
      placeholder="Enter Address"
      id = "address"
      className = "form-control"
      onChange = {this.handleAddressChange} />
    </Form.Group>

    
    
              </Form.Row>

              <Form.Row>
                  
                  <Form.Group as={Col} controlId="email">
                    <Form.Label>Email</Form.Label>
                    <Form.Control type="email" 
                     placeholder="Enter email" 
                     id = "email"
                     className = "form-control"
                     onChange = {this.handleEmailChange}
                     
                     
                     />
                 </Form.Group>
                 
                
                 <Form.Group as={Col} controlId="phonenumber">
          <Form.Label>Phone number</Form.Label>
          <Form.Control type="number" 
          placeholder="Enter Phone number"
          id = "phonenumber"
          className = "form-control"
          onChange = {this.handlePhoneNumberChange} />
        </Form.Group>
    
        
        
                  </Form.Row>

                  <Form.Row>
                  
                  <Form.Group as={Col} controlId="DOB">
                    <Form.Label>Date of birth</Form.Label>
                    <Form.Control type="date" 
                     id = "DOB"
                     className = "form-control"
                     onChange = {this.handleDOBChange}
                     
                     
                     />
                 </Form.Group>
                 
                
                 <Form.Group as={Col} controlId="house">
          <Form.Label>Do you Own -</Form.Label>
          Own 
          <Form.Control type="radio" 
          
          id = "house"
          value = "Own"
          checked = {this.state.house === "Own"}
          className = "form-control"
          onChange = {this.handleHouseChange} />
        
       
            
        </Form.Group>
        <Form.Group as={Col} controlId="house">
        <Form.Label>- Rent a house ?</Form.Label>
        Rent
        <Form.Control type="radio" 
          id = "house"
          value = "Rent"
          checked = {this.state.house === "Rent"}
          className = "form-control"
          onChange = {this.handleHouseChange} />
        </Form.Group>
         
                  </Form.Row>

         

          <Form.Row>
                  
                  <Form.Group as={Col} controlId="familyDescription">
                    <Form.Label>Tell us about your family (adults and children living in your home or visiting regularly).</Form.Label>
                    <textarea type="familyDescription" 
                     placeholder="Enter your famiy description" 
                     id = "familyDescription"
                     className = "form-control"
                     onChange = {this.handlefamilyDescriptionChange}
                    rows = {3}
                  
                     
                     
                     />
                 </Form.Group>
        
                  </Form.Row>
                  <Form.Label>Will the animal have access to: (select all that apply)</Form.Label>
                  <Form.Row>
                  
                  <Form.Group as={Col} controlId="animalAccess">
                    
                    <span>Open Balconies</span>
                    <Form.Control type="radio"
                     checked = {this.state.animalAccess === "Open Balconies"}
                     id = "animalAccess"
                     className = "form-control"
                     onChange = {this.handleanimalAccessChange}
                  
                     
                     
                     />
                 </Form.Group>
        
                  
                  
                  <Form.Group as={Col} controlId="animalAccess">
                    <span>Open Terraces</span>
                    <Form.Control type="radio"
                     checked = {this.state.animalAccess === "Open Terraces"}
                     id = "animalAccess"
                     className = "form-check"
                     onChange = {this.handleanimalAccessChange}
                  
                     
                     
                     />
                 </Form.Group>

                 <Form.Group as={Col} controlId="animalAccess">
                    <span>Open Gardens</span>
                    <Form.Control type="radio"
                     checked = {this.state.animalAccess === "Open Gardens"}
                     id = "animalAccess"
                     className = "form-control"
                     onChange = {this.handleanimalAccessChange}
                  
                     
                     
                     />
                 </Form.Group>
        
                  </Form.Row>

         
          </Form>
          </div>
            {/* <div className="row mt-3">
                <div className="col-md-5 center">
                    <div className="card card-body">
                    <li className="navbar-brand">
                     <a className="navbar-brand" >
                      <img src="petshelterlogo.png" width="40%" className="floatright" alt=""></img>
                    </a>
                     </li>
                        <h1 className="text-center mb-3">
                            <i className="fas fa-sign-in-alt">
                            </i>
                Register
                </h1>


                        <div className="form-group">
                            <label>username</label>
                            <input
                                type="username"
                                id = "username"
                                className="form-control"
                                onChange = {this.handleNameChange}
                           />
                        </div>
                        <div className="form-group">
                            <label>password</label>
                            <input
                                type="password"
                                id="password"
                                name="password"
                                className="form-control"
                                onChange= {this.handlePasswordChange}
                         />
                        </div>

                        <div className="form-group">
                            <label>age</label>
                            <input
                                type="date"
                                id= "age"
                                className="form-control"
                                onChange= {this.handleAgeChange}
                         />
                        </div>

                        <div className="form-group">
                            <label>address</label>
                            <input
                                type="address"
