import React, { Component,useState,Switch,Text } from 'react';
import { getUsers, logIn } from "../actions/actions";
import '../css/Table.css';
import '../App.css';


class Loginpage extends Component {
    
    constructor() {
        
        super();
        
        this.state = {
            username: "",
            password: "",
            rememberMe: false,
            data: [],
        };
        
    }

    componentDidMount() {
        
    this.retrieveUsers();
    
    }

    retrieveUsers = () => {
        getUsers().then((res) =>{
        this.setState({data: res});
        console.log(res);
    });
}
toggleRememberMe = value => {
    this.setState({ rememberMe: value })
      if (value === true) {
    //user wants to be remembered.
      this.rememberUser();
    } else {
      this.forgetUser();
    }
  }
  rememberUser = async () => {
    try {
      await AsyncStorage.setItem('YOUR-KEY', this.state.username);
    } catch (error) {
      // Error saving data
    }
    };
    getRememberedUser = async () => {
    try {
      const username = await AsyncStorage.getItem('YOUR-KEY');
      if (username !== null) {
        // We have username!!
        return username;
      }
    } catch (error) {
      // Error retrieving data
    }
    };
    forgetUser = async () => {
      try {
        await AsyncStorage.removeItem('Longtail-User');
      } catch (error) {
       // Error removing
      }
    };
    handleNameChange = (event) => {
        event.preventDefault();
        this.setState({ username: event.target.value });
    };

    handlePasswordChange = (event) => {
        event.preventDefault();
        this.setState({ password: event.target.value });
    };

    // authenticator = (event) => {
    //     const {username} = this.state;
    //     const{password} = this.state;
      
    //     const contents = this.state.data.forEach((item,key)=> {
            
