    //         }
            
            
    //     });
        
        
        
    // };


    authenticatorr = (event) => {
        event.preventDefault();
        const {username} = this.state;
        const{password} = this.state;
       
        let data = {
            username: username,
            password: password,
        }
        logIn(data).then((res) =>{
            console.log(res)
            
             if(res === true){
                localStorage.setItem('username', username)
                console.log(username)
                 alert("You have successfully logged in");
                 this.props.history.push("/Home");
             }
             else { 
                 alert("wrong username or password");
             }
             
        })
        
        
    };


    
    render() {
        return (

            
            <div className="row mt-3">
                
                <div className="col-md-6-m-auto">
               
                    <div className="card card-body">
                    <li className="navbar-brand">
                     <a className="navbar-brand" href="/Home">
                      <img src="pharmacylogo.png" width="100%" height="100%" className="floatright" alt=""></img>
                    </a>
                     </li>
                        <h1 className="text-center mb-3">
                            <i className="fas fa-sign-in-alt">

                           
                            </i>
                           
                Login
                </h1>

                
                
                        <div className="form-group">
                            <label>username</label>
                            <input
                                type="username"
                                id = "username"
                                className="form-control"
                                onChange = {this.handleNameChange}
                           />
                        </div>
                        <div className="form-group">
                            <label>password</label>
                            <input
                                type="password"
                                id="password"
                                className="password"
                                className="form-control"
                                onChange= {this.handlePasswordChange}
                         />
                        </div>

                        <div className="form-group">
                            <label>Remember me</label>
                            <input
                            value={this.state.rememberMe}
                                type="checkbox"
                                id="password"
                                className="password"
                                className="form-control"
                                onValueChange={(value) => this.toggleRememberMe(value)}
                         />
                        </div>

                        <div>
                         <img src="PharmacyDesign.jpg" width = "100% height = 100%"></img>
                          </div>
                         

                        <button
                            id="1"
                            name="btn"
                            type="submit"
                            onClick= {this.authenticatorr}
                            className = "btn btn-success"
                        >
                            Login
                        
                        </button>



                        <p className = "lead mt-4">
                            Create Account <a href = "/registration">Register</a>
                        </p>
                        

                 </div>
                 </div >
                 </div > 
                 
    );
    }

}

export default Loginpage;
