import React, { Component } from 'react';
import {Link} from 'react-router-dom'

import { getHealthcareProduct} from "../actions/actions";
import { deleteHealthcareProduct,updateHealthcareProduct } from "../actions/actions";



class HealthcareProductsOrdered extends Component {
    constructor(){
        super();
        // this.deleteRecord = this.deleteRecord.bind(this);
        this.fillForm = this.fillForm.bind(this);
        this.state = {
          data: [],
        };
    }
   
    componentDidMount() {
        this.retrieveUsers();
    }

    retrieveUsers = () => {
        getHealthcareProduct().then((res) =>{
        this.setState({data: res});
        console.log(res);
    });
}

deleteRecord = (event) => {
  event.preventDefault();
  let id = event.target.id;
  let data ={
    id:id,
  }
  console.log(data);
  deleteHealthcareProduct(data).then(res=>{
      alert("health care product deleted");
  }).catch(err=>{
      alert("error deleting");
  });
};

  fillForm = item => event => {
    event.preventDefault();
    this.props.history.push("/updateHealthcareProduct", {
      healthcareid: item.healthcareid,
      healthcareproduct: item.healthcareproduct,
      healthcarequantity: item.healthcarequantity,
      userid: item.userid,
    });
  }


    render() {
        let rows = [];

        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(
            <tr className='bg-secondary text-white'>
            <td>{item.healthcareid}</td>
            <td>{item.healthcareproduct}</td>
            <td>{item.healthcarequantity}</td>
            <td>{item.userid}</td>
           

            <td><button onClick={this.fillForm(item)}>Edit</button></td>
            <td><button id={item.healthcareid} onClick={this.deleteRecord}>Delete</button></td>
            </tr>
            );
          }else{
            rows.push(<tr className='bg-dark text-light'>
            <td>{item.healthcareid}</td>
            <td>{item.healthcareproduct}</td>
            <td>{item.healthcarequantity}</td>
            <td>{item.userid}</td>
          

            <td><button className="btn bg-info" onClick={this.fillForm(item)}>Edit</button></td>
            <td><button id={item.healthcareid} onClick={this.deleteRecord}>Delete</button></td>
            </tr>);
            
          }
     })
        return (
          <div className="container">
            <Link to="/Home">
        <img src="pharmacylogo.png" width="20%" height="20%" className="d-inline-block align-top" alt=""></img>
    </Link>
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                <th>product ID</th>
                <th>healthcare product name</th>
                <th>quantity</th>
                <th>User ID</th>
                <th>Edit</th>
                <th>Delete</th>
              </tr>
              </thead>
              {rows}
            </table>
                       <Link to='/addHealthcareProduct'>
                         <button type="button" className="btn btn-dark btn-lg btn-block">Add New Health Care Product</button>
                        
                       </Link>
                        
                       
            <div className="row">
            </div>
          </div>
        );
      }
}


export default HealthcareProductsOrdered;
