import React, { Component } from 'react';
import {Link} from "react-router-dom";
import { registerBmiDetail } from "../actions/actions";



class BmiPage extends Component {
    constructor() {
        super();
        this.handleHeightChange = this.handleHeightChange.bind(this);
        this.handleWeightChange = this.handleWeightChange.bind(this);
        //this.BmiCalculate - this.BmiCalculate.bind(this);
        this.state = {
            id: "",
            height: "",
            weight: "",
            data: []
        };
    }

    componentDidMount() {

    }
    RegisterClickHandler = () =>
    {
       const {id} = this.state;
       const {height} = this.state;
       const {weight} = this.state;
       let data = {
           id: id,
           height: height,
           weight: weight,        
       }
       registerBmiDetail(data).then((res) =>{
            try{
                alert("You have successfully added");
                this.props.history.push("/");
            }catch(error){
                console.log("Error while adding" + error);
            }
            
       })
    };


    handleidChange = (event) => {
        event.preventDefault();
        this.setState({ id: event.target.value });
    };


    handleHeightChange(event) {
        this.setState({ height: event.target.value });
    };

    handleWeightChange(event) {
        this.setState({ weight: event.target.value });
    };
    BmiCalculate= () => {
       const {height} = this.state;
       const {weight} = this.state;
       let data = {
           height: height,
           weight: weight,        
       }
        let bmiValue = ( weight / (height * 100)) / (height * 100);
      this.setState({ bmi : bmiValue });
      let bmiClass = this.getBmi(bmiValue);
      this.setState({ bmiClass : bmiClass });
    }
    getBmi(bmi) {
        if(bmi < 18.5) {
            return "Underweight";
        }
        if(bmi >= 18.5 && bmi < 24.9) {
            return "Normal weight";
        }
        if(bmi >= 25 && bmi < 29.9) {
            return "Overweight";
        }
        if(bmi >= 30) {
            return "Obesity";
        }
    }

    render() {
        return (
            <div className="row mt-5">
                <div className="col-md-6-m-auto">
                    <div className="card card-body">
                    <li className="navbar-brand">
                     <a className="navbar-brand" href="/Home">
                      <img src="pharmacylogo.png" width="100%" height="100%" className="floatright" alt=""></img>
                    </a>
                     </li>
                        <h1 className="text-center mb-3">
                            <i className="fas fa-sign-in-alt">
                            </i>
                Register
                </h1>
   
                        <div className="form-group">
                            <label>height</label>
                            <input
                                type="number"
                                id = "height"
                                placeholder = "Enter your height in centimeters please"
                                className="form-control"
                                onChange = {this.handleHeightChange}
                           />
                        </div>
                        <div className="form-group">
                            <label>weight</label>
                            <input
                                type="number"
                                id="weight"
                                placeholder = "Enter your weight in KG please"
                                name="weight"
                                className="form-control"
                                onChange= {this.handleWeightChange}
                         />
                        </div>
                        <div>
                         <img src="PharmacyDesign.jpg" width = "100% height = 100%"></img>
                          </div>
                        <button
                            type="submit"
                            onClick= {this.BmiCalculate}
                            className = "btn btn-dark btn-block"
                        >
                            Submit
                            
                        </button>
                        <div className="row">
    <   h3>BMI = {this.state.bmi}</h3>
    </div>
    <div className="row">
    <h3>h = {this.state.bmiClass}</h3>
        </div>
                 </div>
                 </div >
                 </div > 
                 
    );
    }

}

export default BmiPage;
