import React, {Component} from 'react';
import { Chart }  from './Chart';
import { Chartt } from 'react-chartjs-2';


class ChartPage extends Component{

    constructor() {
        super();
        this.state={
            chartData:{

            }
        }
    }


    componentDidMount() {
        this.getchartData();
    }


    getchartData = () => {

        fetch("http://localhost:3001/getBmi")
            .then(res => res.json())
            .then((jsonarray) => {
                var labels = jsonarray.map(function(e) {
                    return e.id;
                });
                var data = jsonarray.map(function(e) {
                    return e.bmiResult;
                });

                console.log(labels,data);

                this.setState({
                    chartData:{
                        labels:labels,
                        datasets:[
                            {
                                label:'Chart',

                                data:data
                            }
                        ]
                    }
                });
            });
    };   

    render(){
        return(
            <div className="App">
                <header className="App-header">

                    <Chartt chartData={this.state.chartData} location="Test" legendPosition="bottom"/>
                </header>
            </div>

        );
    }
}

export default ChartPage;
