import React, { Component } from 'react';
import {connect} from 'react-redux';
// import '../css/Table.css';
import UserForm from './UserForm';
import { getUsers, getUser, createUser, deleteUser } from "../actions/actions";
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import s from './Table.scss';
import actions from '../redux/actions/users';
import { Button } from 'react-bootstrap';


class Table extends Component {
  constructor(){
    super();
    this.deleteRecord = this.deleteRecord.bind(this);
    this.fillForm = this.fillForm.bind(this);
    this.state = {
      data: [],
      message: '',
      dateRetrieved: '',
    }
  }

  redirectToHome = () => {
    const { history } = this.props;
    if(history) history.push('/UserForm');
  }
  componentDidMount() {
    this.props.dispatch(actions.getUsers());
  }

  actionFormatter(cell) {
    return (
      <div className={`d-flex justify-content-between`}>
        <Button
          className="btn-danger"
          size="xs"
          onClick={() => this.deleteRecord(cell)}
        >
          Delete
        </Button>
      </div>
     )
  }


  // deleteRecord = (event) =>{
  //   event.preventDefault();
  //   let user_id = event.target.user_id;
  //   deleteUser(user_id).then(res=>{
  //     alert("user deleted");
  //   }).catch(err=>{
  //     alert("error deleting");
  //   })
  // }

  deleteRecord = cell =>{
    alert(cell);
    // let user_id = event.target.user_id;
    // this.props.dispatch(actions.deleteUser(user_id));
  }

  fillForm = item => event => {
    event.preventDefault();
    this.props.history.push("/UserForm", {
      user_id: item.user_id,
      user_name: item.user_name,
      user_age: item.user_age,
      user_occupation: item.user_occupation,
      user_hobby: item.user_hobby
    });
  }
  render() {
    const selectRow = {
      mode: 'radio',
      hideSelectColumn: true,
      clickToSelect: true,
      bgColor: 'green'
    };
    const options = {
      sortName: 'user_id',
      sortOrder: 'asc',
      sizePerPage: 50,
      paginationSize: 5,
      sizePerPageDropDown: this.renderSizePerPageDropDown,
      prePage: 'Prev', // Previous page button text
      nextPage: 'Next', // Next page button text
    };
    
    return (
      <div className="container">
        <BootstrapTable bordered={false} data={this.props.users} version="4" pagination options={options}
                search tableContainerClass={`table-responsive table-striped table-hover ${s.usersListTableMobile}`}
                selectRow={selectRow} >

                <TableHeaderColumn dataField="user_id" isKey>
                  <span className="fs-sm">ID</span>
                </TableHeaderColumn>

                <TableHeaderColumn dataField="user_name" dataSort>
                  <span className="fs-sm">Name</span>
                </TableHeaderColumn>

                <TableHeaderColumn dataField="user_username" dataSort>
                  <span className="fs-sm">Username</span>
                </TableHeaderColumn>

                <TableHeaderColumn dataField="user_password" dataSort>
                  <span className="fs-sm">Password</span>
                </TableHeaderColumn>

                <TableHeaderColumn dataField="user_occupation" dataSort>
                  <span className="fs-sm">Occupation</span>
                </TableHeaderColumn>

                <TableHeaderColumn dataField="user_age" dataSort>
                  <span className="fs-sm">Age</span>
                </TableHeaderColumn>

                <TableHeaderColumn dataField="user_hobby" dataSort>
                  <span className="fs-sm">Hobby</span>
                </TableHeaderColumn>

                <TableHeaderColumn dataFormat={this.actionFormatter.bind(this)}>
                  
                </TableHeaderColumn>
          </BootstrapTable>
      </div>
    );
  }
}

const mapStateToProps = state =>({
  users: state.userReducer.users,
})

export default connect(mapStateToProps)(Table);
