import React, { Component } from 'react';
import { getBmi } from "../actions/actions";
import { deleteUser } from "../actions/actions";
import {Link} from 'react-router-dom'

class myProfilePage extends Component {
    constructor(){
        super();
        this.deleteRecord = this.deleteRecord.bind(this);
        this.fillForm = this.fillForm.bind(this);
        this.state = {
          data: []
        };
    }
   
    componentDidMount() {
        this.retrieveBmi();
    }

    retrieveBmi = () => {
        getBmi().then((res) =>{
        this.setState({data: res});
        console.log(res);
    });
}

addRecord = () => event => {
    event.preventDefault();
    this.props.history.push("/UserForm");
  };

deleteRecord = (event) => {
    event.preventDefault();
    let user_id = event.target.id;
    console.log(user_id);
    deleteUser(user_id).then(res=>{
        alert("user deleted");
    }).catch(err=>{
        alert("error deleting");
    });
};

  fillForm = item => event => {
    event.preventDefault();
    this.props.history.push("/UserForm", {
      user_id: item.user_id,
      height: item.height,
      weight: item.weight
    });
  }

    render() {
        let rows = [];

        
        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(
              <thead>
            <tr className='bg-secondary text-white'>
            <td>{item.user_id}</td>
            <td>{item.height}</td>
            <td>{item.weight}</td>
            <td><button onClick={this.fillForm(item)}>Edit</button></td>
            <td><button id={item.user_id} onClick={this.deleteRecord}>Delete</button></td>
            </tr>
            </thead>
            );
          }else{
            rows.push(
            <thead>
            <tr className='bg-dark text-light'>
            <td>{item.user_id}</td>
            <td>{item.height}</td>
            <td>{item.weight}</td>
            <td><button className="btn bg-info" onClick={this.fillForm(item)}>Edit</button></td>
            <td><button id={item.user_id} onClick={this.deleteRecord}>Delete</button></td>
            </tr>
            </thead>
            
            );
            
          }
     })
        return (
          
          <div className="container">
            <Link to="/Home">
        <img src="pharmacylogo.png" width="20%" height="20%" className="d-inline-block align-top" alt=""></img>
    </Link>
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                <th>user_id</th>
                <th>height</th>
                <th>weight</th>
                <th>Edit</th>
                <th>Delete</th>
              </tr>
              </thead>
              {rows}
            </table>
                       <Link to='/BmiPage'>
                         <button type="button" className="btn btn-dark btn-lg btn-block">Add New Bmi record</button>
                        
                       </Link>
     
            <div className="row">
            </div>
          </div>
        );
      }
}


export default myProfilePage;
