import React, { Component } from 'react';
import {Link} from "react-router-dom";
import { registerBmiDetail } from "../actions/actions";



class BmiPage extends Component {
    constructor() {
        super();
        this.handleHeightChange = this.handleHeightChange.bind(this);
        this.handleWeightChange = this.handleWeightChange.bind(this);
        //this.BmiCalculate - this.BmiCalculate.bind(this);
        this.state = {
            id: "",
            height: "",
            weight: "",
            // user_id: "",
            bmiResult: "",
            bmiClass: "",
            data: []
        };
    }

    componentDidMount() {

    }
    RegisterClickHandler = () =>
    {
       const {id} = this.state;
       const {height} = this.state;
       const {weight} = this.state;
       const {bmiResult} = this.state;
       const {bmiClass} = this.state;
       //const {user_id} = this.state;
       let data = {
           id: id,
           height: height,
           weight: weight, 
           bmiResult: bmiResult,
           bmiClass: bmiClass,     
           //user_id: user_id,  
       }
       registerBmiDetail(data).then((res) =>{
            try{
                alert("You have successfully added");
                this.props.history.push("/");
            }catch(error){
                console.log("Error while adding" + error);
            }
            
       })
    };


    handleuser_idChange = (event) => {
        event.preventDefault();
        this.setState({ user_id: event.target.value });
    };


    handleHeightChange(event) {
        this.setState({ height: event.target.value });
    };

    handleWeightChange(event) {
        this.setState({ weight: event.target.value });
    };
    handleBmiResultChange = (event) => {
        event.preventDefault();
        this.setState({ bmiResult: event.target.value });
    };
    handleBmiClassChange = (event) => {
        event.preventDefault();
        this.setState({ bmiClass: event.target.value });
    };


    BmiCalculate= () => {
       const {height} = this.state;
       const {weight} = this.state;
       let data = {
           height: height,
           weight: weight,        
       }
      this.bmiResult = ( weight / (height) * 100) / (height) * 100;
      this.setState({ bmiResult : this.bmiResult });
     this.bmiClass = this.getBmi(this.bmiResult);
      this.setState({ bmiClass : this.bmiClass });
    }
    getBmi(bmiResult) {
        if(bmiResult < 18.5) {
            return "Underweight";
        }
        if(bmiResult >= 18.5 && bmiResult <= 24.99) {
            return "Normal weight";
        }
        if(bmiResult >= 25 && bmiResult <= 29.99) {
            return "Overweight";
        }
        if(bmiResult >= 30 && bmiResult <90) {
            return "Obesity";
        }
        if(bmiResult >= 90) {
            return "DEAD";
        }
    }

    render() {
        return (
            <div className="row mt-5">
                <div className="col-md-6-m-auto">
                    <div className="card card-body">
                    <li className="navbar-brand">
                     <a className="navbar-brand" href="/Home">
                      <img src="pharmacylogo.png" width="100%" height="100%" className="floatright" alt=""></img>
                    </a>
                     </li>
                        <h1 className="text-center mb-3">
                            <i className="fas fa-sign-in-alt">
                            </i>
                Add your Details !
                </h1>
   
                        <div className="form-group">
                            <label>height</label>
                            <input
                                type="number"
                                id = "height"
                                placeholder = "Enter your height in centimeters please"
                                className="form-control"
                                onChange = {this.handleHeightChange}
                           />
                        </div>
                        <div className="form-group">
                            <label>weight</label>
                            <input
                                type="number"
                                id="weight"
                                placeholder = "Enter your weight in KG please"
                                name="weight"
                                className="form-control"
                                onChange= {this.handleWeightChange}
                         />
                        </div>
                        {/* <div>
                         <img src="PharmacyDesign.jpg" width = "100% height = 100%"></img>
                          </div> */}
                        <button
                            type="submit"
                            onClick= {this.BmiCalculate}
                            className = "btn btn-dark btn-block"
                        >
                            Calculate
                            
                        </button>

                        <div className="form-group">
                            <label>Bmi Result</label>
                            <input
                                type="label"
                                id="bmiResult"
                                value = {this.state.bmiResult}
                                editable = {true}
                                placeholder = "result of the bmi will be calculated here"
                                name="bmiResult"
                                className="form-control"
                                onChange= {this.handleBmiResultChange}
                         />
                        </div>

                        <div className="form-group">
                            <label>Your Bmi Class</label>
                            <input
                                type="label"
                                id="bmiClass"
                                value = {this.state.bmiClass}
                                editable = {false}
                                placeholder = "Your bmi class will be calculated here"
                                name="bmiClass"
                                className="form-control"
                                onChange= {this.handleBmiClassChange}
                         />
                        </div>

                        <button
                            type="submit"
                            onClick= {this.RegisterClickHandler}
                            className = "btn btn-dark btn-block"
                        >
                            Submit
        
                        </button>


                 </div>
                 </div >
                 </div > 
                 
    );
    }

}

export default BmiPage;
