import React, { Component } from 'react';
import {Link} from 'react-router-dom'

import { getLabTest} from "../actions/actions";
import { deleteLabTest } from "../actions/actions";
import { getUsers } from "../actions/actions";


class LabTestReservations extends Component {
    constructor(){
        super();
        // this.deleteRecord = this.deleteRecord.bind(this);
        this.fillForm = this.fillForm.bind(this);
        this.state = {
          data: [],
        };
    }
   
    componentDidMount() {
        this.retrieveUsers();
    }

    retrieveUsers = () => {
        getLabTest().then((res) =>{
        this.setState({data: res});
        console.log(res);
    });
}

deleteRecord = (event) => {
  event.preventDefault();
  let id = event.target.id;
  let data ={
    id:id,
  }
  console.log(data);
  deleteLabTest(data).then(res=>{
      alert("user deleted");
  }).catch(err=>{
      alert("error deleting");
  });
};

  fillForm = item => event => {
    event.preventDefault();
    this.props.history.push("/MedicineForm", {
      medicineid: item.medicineid,
      medicinename: item.medicinename,
      userid: item.userid,
    });
  }


    render() {
        let rows = [];

        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(
            <tr className='bg-secondary text-white'>
            <td>{item.labtestid}</td>
            <td>{item.labtestdate}</td>
            <td>{item.userid}</td>
           

            <td><button onClick={this.fillForm(item)}>Edit</button></td>
            <td><button id={item.labtestid} onClick={this.deleteRecord}>Delete</button></td>
            </tr>
            );
          }else{
            rows.push(<tr className='bg-dark text-light'>
            <td>{item.labtestid}</td>
            <td>{item.labtestdate}</td>
            <td>{item.userid}</td>
          

            <td><button className="btn bg-info" onClick={this.fillForm(item)}>Edit</button></td>
            <td><button id={item.labtestid} onClick={this.deleteRecord}>Delete</button></td>
            </tr>);
            
          }
     })
        return (
          <div className="container">
            <Link to="/Home">
        <img src="pharmacylogo.png" width="20%" height="20%" className="d-inline-block align-top" alt=""></img>
    </Link>
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                <th>labtestid</th>
                <th>labtestdate</th>
                <th>userid</th>
                <th>Edit</th>
                <th>Delete</th>
              </tr>
              </thead>
              {rows}
            </table>
                       <Link to='/addLabTest'>
                         <button type="button" className="btn btn-dark btn-lg btn-block">Add New Lab Reservation</button>
                        
                       </Link>
                        
                       
            <div className="row">
            </div>
          </div>
        );
      }
}


export default LabTestReservations;
